#!/usr/bin/env python3

DNA = ''
 
DNA.upper()#upper casing the DNA sequence

upper_DNA = DNA.upper()#using the upper case DNA as the new variable

upper_DNA.count('A')#counting the number of As
As = upper_DNA.count('A')

upper_DNA.count('T')#counting the number of Ts
Ts = upper_DNA.count('T')

upper_DNA.count('C')#counting the number of Cs
Cs = upper_DNA.count('C')

upper_DNA.count('G')#counting the number of Gs
Gs = upper_DNA.count('G')

print('A = '+str(As))#printing the results
print('T = '+str(Ts))
print('C = '+str(Cs))
print('G = '+str(Gs))




