#!/usr/bin/env python3

DNA = ''

#DNA = 'CGGAATTCTG'

DNA.find('GAATTC')#finds the beginning of the EcoR1 site
EcoR1beg = DNA.find('GAATTC')

int(EcoR1beg)#makes the value an integer
EcoR1start = int(EcoR1beg)+1

#print(EcoR1start)

EcoR1end = EcoR1start+5

message = "The beginning of the EcoR1 is at nucleotide {} and the end of the EcoR1 is at nucleotide {}"

message.format(EcoR1start,EcoR1end)
print(message.format(EcoR1start,EcoR1end))
