#!/usr/bin/env python3

DNA = ''

#DNA = 'AATTCCGG'

DNA.count('A')#counting the number of As
As = DNA.count('A')

DNA.count('T')#counting the number of Ts
Ts = DNA.count('T')

As_and_Ts = As + Ts#the total number of As and Ts in the sequence

len(DNA)#calculating the length of the total DNA sequence
TotalDNA = len(DNA)

print(As_and_Ts/TotalDNA,'%',sep='')#dividing the number of As/Ts by the total number of DNA, putting this in a percent, and then taking out the separator using the sep= function); finally printing the answer to the screen







