#!/usr/bin/env python3

DNA = ''

DNA.lower()#lower case the entire sequence
DNA_lower = DNA.lower()

DNA_lower.replace('a','T')#start replacing with the complement nucleotide
As_replaced = DNA_lower.replace('a','T')

As_replaced.replace('t','A')
Ts_replaced = As_replaced.replace('t','A')

Ts_replaced.replace('c','G')
Cs_replaced = Ts_replaced.replace('c','G')

Cs_replaced.replace('g','C')
Gs_replaced = Cs_replaced.replace('g','C')

Gs_replaced[::-1]#revese the sequence
print(Gs_replaced[::-1])#print reversed
