#Q11 making a set of a DNA sequence and only returning the unique letters in the string
#DNA = set('')
#print(DNA)

DNA_set = set('')

DNA = ''

nucleotide_number = {}#12 making a dictionary to count the number of nucleotides in a sequence

unique_nuc=DNA_set

for nucleotide in unique_nuc:
	#DNA.count(nucleotide)
	nucleotide_number[nucleotide] = DNA.count(nucleotide)		
print(nucleotide_number)

len(DNA)
total_DNA_len = len(DNA)

nucleotide_comp = {}

for nucleotide in unique_nuc:
	nucleotide_comp[nucleotide] = int(DNA.count(nucleotide))/int(total_DNA_len)
print(nucleotide_comp)
	
for nucleotide in nucleotide_comp:
	#print(nucleotide,'is ', nucleotide_comp[nucleotide])
	print('{} is {:.3%}'.format(nucleotide, nucleotide_comp[nucleotide]))
message = nucleotide_comp['C']+nucleotide_comp['G']

print('GC content is {:.3%}'.format(message))

 
